import React, { useState, useEffect } from 'react';
import { FaBook, FaChalkboardTeacher, FaClock, FaCalendarAlt, FaPlus } from 'react-icons/fa';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function App() {
  const baseUrl = 'http://13.51.11.25:8080/api/timetable';

  const [courses, setCourses] = useState([]);
  const [courseCode, setCourseCode] = useState('');
  const [courseName, setCourseName] = useState('');
  const [instructorName, setInstructorName] = useState('');

  const [timeSlots, setTimeSlots] = useState([]);
  const [day, setDay] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [slotCourseCode, setSlotCourseCode] = useState('');

  const fetchCourses = async () => {
    const res = await fetch(`${baseUrl}`);
    const data = await res.json();
    setCourses(data.courses || []);
    setTimeSlots(data.timeSlots || []);
  };

  const handleCourseSubmit = async (e) => {
    e.preventDefault();
    const newCourse = { courseCode, courseName, instructorName };
    try {
      const res = await fetch(`${baseUrl}/course`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newCourse),
      });
      if (res.ok) {
        toast.success('‚úÖ Course added successfully!');
        setCourseCode('');
        setCourseName('');
        setInstructorName('');
        fetchCourses();
      } else {
        toast.error('‚ùå Failed to add course');
      }
    } catch (err) {
      toast.error('‚ùå Network error');
    }
  };

  const handleTimeSlotSubmit = async (e) => {
    e.preventDefault();
    const newSlot = { day, startTime, endTime, courseCode: slotCourseCode };
    try {
      const res = await fetch(`${baseUrl}/timeslot`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newSlot),
      });
      if (res.ok) {
        toast.success('‚úÖ Time slot added successfully!');
        setDay('');
        setStartTime('');
        setEndTime('');
        setSlotCourseCode('');
        fetchCourses();
      } else {
        toast.error('‚ùå Failed to add time slot');
      }
    } catch (err) {
      toast.error('‚ùå Network error');
    }
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  return (
    <div className="container">
      <ToastContainer position="top-right" autoClose={3000} />
      <h1><FaBook /> Timetable Manager</h1>

      <form className="course-form" onSubmit={handleCourseSubmit}>
        <h2><FaPlus /> Add Course</h2>
        <input type="text" placeholder="Course Code" value={courseCode} onChange={(e) => setCourseCode(e.target.value)} required />
        <input type="text" placeholder="Course Name" value={courseName} onChange={(e) => setCourseName(e.target.value)} required />
        <input type="text" placeholder="Instructor Name" value={instructorName} onChange={(e) => setInstructorName(e.target.value)} required />
        <button type="submit"><FaPlus /> Add Course</button>
      </form>

      <form className="timeslot-form" onSubmit={handleTimeSlotSubmit}>
        <h2><FaPlus /> Add TimeSlot</h2>
        <input type="text" placeholder="Day (e.g. Monday)" value={day} onChange={(e) => setDay(e.target.value)} required />
        <input type="time" value={startTime} onChange={(e) => setStartTime(e.target.value)} required />
        <input type="time" value={endTime} onChange={(e) => setEndTime(e.target.value)} required />
        <input type="text" placeholder="Course Code" value={slotCourseCode} onChange={(e) => setSlotCourseCode(e.target.value)} required />
        <button type="submit"><FaPlus /> Add TimeSlot</button>
      </form>

      <div className="course-list">
        <h2>üìö All Courses</h2>
        <ul>
          {courses.map((course) => (
            <li key={course.courseCode}>
              <strong>{course.courseCode}</strong>: {course.courseName}<br />
              <FaChalkboardTeacher /> <em>{course.instructorName}</em>
            </li>
          ))}
        </ul>
      </div>

      <div className="timeslot-list">
        <h2>‚è∞ All TimeSlots</h2>
        <ul>
          {timeSlots.map((slot, index) => (
            <li key={index}>
              <FaCalendarAlt /> <strong>{slot.day}</strong> | <FaClock /> {slot.startTime} - {slot.endTime} | <FaBook /> <em>{slot.courseCode}</em>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
