import React, { useState, useEffect } from 'react';

export default function App() {
  const [courses, setCourses] = useState([]);
  const [code, setCode] = useState('');
  const [name, setName] = useState('');
  const [instructor, setInstructor] = useState('');

  const fetchCourses = async () => {
    const res = await fetch('/api/timetable/course');
    const data = await res.json();
    setCourses(data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newCourse = { code, name, instructor };
    await fetch('/api/timetable/course', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newCourse),
    });
    setCode('');
    setName('');
    setInstructor('');
    fetchCourses();
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  return (
    <div className="container">
      <h1>üìò Timetable Manager</h1>

      <form className="course-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Course Code"
          value={code}
          onChange={(e) => setCode(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Course Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Instructor Name"
          value={instructor}
          onChange={(e) => setInstructor(e.target.value)}
          required
        />
        <button type="submit">Add Course</button>
      </form>

      <div className="course-list">
        <h2>üìö All Courses</h2>
        <ul>
          {courses.map((course) => (
            <li key={course.code}>
              <strong>{course.code}</strong>: {course.name}<br />
              üë®‚Äçüè´ <em>{course.instructor}</em>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
